class WorkThread(QThread):
    # 自定义信号对象
    trigger = pyqtSignal(str)

    def __int__(self):
        # 初始化函数
        super(WorkThread, self).__init__()

    def run(self):
        def up_file(source_file, target_folder):
            # 设置SSH连接参数
            ssh = paramiko.SSHClient()
            ssh.set_missing_host_key_policy(paramiko.AutoAddPolicy())
            ssh.connect(hostname='43.242.96.52',
                        username='xiaorunze',
                        password='hiplot!@#',
                        port=41526)

            # 执行命令
            stdin, stdout, stderr = ssh.exec_command('rm -r /home/xiaorunze/defense/data/*')

            # 执行命令
            stdin, stdout, stderr = ssh.exec_command('rm -r /home/xiaorunze/defense/out/*')

            # 使用SFTP传输文件
            sftp = ssh.open_sftp()
            sftp.put(source_file, target_folder)  # 目标文件名可以按需更改
            sftp.close()

            # 关闭SSH连接
            ssh.close()

        def run_command(fasta, out, coverage):
            # 创建SSH对象
            ssh = paramiko.SSHClient()

            # 允许连接不在know_hosts文件中的主机
            ssh.set_missing_host_key_policy(paramiko.AutoAddPolicy())

            # 连接服务器
            ssh.connect(hostname='43.242.96.52',
                        username='xiaorunze',
                        password='hiplot!@#',
                        port=41526)

            # 执行命令
            stdin, stdout, stderr = ssh.exec_command('python /home/xiaorunze/defense/run_python.py -f %s -o %s -c %s' % (fasta, out, coverage))

            # 执行命令
            stdin, stdout, stderr = ssh.exec_command('bash /home/xiaorunze/defense/run_defense.sh')

            # 获取命令结果
            result = stdout.read().decode('utf8')
            print(result)  # 如果有输出的话

            # 关闭连接
            ssh.close()

        def down_from_remote(sftp_obj, remote_dir_name, local_dir_name):
            """远程下载文件"""
            remote_file = sftp_obj.stat(remote_dir_name)
            if isdir(remote_file.st_mode):
                # 文件夹，不能直接下载，需要继续循环
                check_local_dir(local_dir_name)
                print('开始下载文件夹：' + remote_dir_name)
                for remote_file_name in sftp.listdir(remote_dir_name):
                    sub_remote = os.path.join(remote_dir_name, remote_file_name)
                    sub_remote = sub_remote.replace('\\', '/')
                    sub_local = os.path.join(local_dir_name, remote_file_name)
                    sub_local = sub_local.replace('\\', '/')
                    down_from_remote(sftp_obj, sub_remote, sub_local)
            else:
                # 文件，直接下载
                print('开始下载文件：' + remote_dir_name)
                sftp.get(remote_dir_name, local_dir_name)

        def check_local_dir(local_dir_name):
            """本地文件夹是否存在，不存在则创建"""
            if not os.path.exists(local_dir_name):
                os.makedirs(local_dir_name)

        try:
            time.sleep(3)
            self.trigger.emit('Updating the file!!!')
            up_file(fasta, '/home/xiaorunze/defense/data/' + os.path.basename(fasta))

            self.trigger.emit('Running the defensefinder for about 3 mins!!!')
            run_command(os.path.basename(fasta),
                        str(os.path.split(out)[-1]),
                        coverage)

            host_name = '43.242.96.52'
            user_name = 'xiaorunze'
            password = 'hiplot!@#'
            port = 41526
            # 远程文件路径（需要绝对路径）
            remote_dir = '/home/xiaorunze/defense/out/' + str(os.path.split(out)[-1]) + '/'
            # 本地文件存放路径（绝对路径或者相对路径都可以）
            local_dir = out

            # 连接远程服务器
            t = paramiko.Transport((host_name, port))
            t.connect(username=user_name, password=password)
            sftp = paramiko.SFTPClient.from_transport(t)
            # 远程文件开始下载
            down_from_remote(sftp, remote_dir, local_dir)
            # 关闭连接
            t.close()

            self.trigger.emit('Finished!!!')

        except Exception as ex:
            self.trigger.emit('Some errors have occurred, %s!' % ex)


