class WorkThread(QThread):
    # 自定义信号对象
    trigger = pyqtSignal(str)

    def __int__(self):
        # 初始化函数
        super(WorkThread, self).__init__()

    def run(self):
        def check_process_running(process_name):  # 检查进程是否运行
            for process in psutil.process_iter(['name']):
                if process.info['name'] == process_name:
                    return True
            return False

        try:
            if type == 'A':
                os.popen(r"./tools/iqtree/bin/iqtree -s %s -pre %s -redo -m MFP -b %s"
                         % (fasta, out, bootstrap))
            elif type == 'B':
                os.popen(r"./tools/iqtree/bin/iqtree -s %s -pre %s -redo -m MF -b %s"
                         % (fasta, out, bootstrap))
            elif type == 'C':
                os.popen(r"./tools/iqtree/bin/iqtree -s %s -pre %s -redo -m TIM2+I+G -b %s"
                         % (fasta, out, bootstrap))
            elif type == 'D':
                os.popen(r"./tools/iqtree/bin/iqtree -s %s -pre %s -redo"
                         % (fasta, out))

            process_name = 'iqtree'
            time.sleep(5)
            while True:  # 判断 iqtree.exe 是否运行完成
                if check_process_running(process_name):
                    print(f"The process {process_name} is running.")
                    time.sleep(30)
                    continue
                else:
                    print(f"The process {process_name} is not running.")
                    break

            self.trigger.emit('Finished!!!')

        except Exception as ex:
            self.trigger.emit('Some errors have occurred, %s!' % ex)


